import{c as a,w as t,o as r,b as i,u as d,X as o,a as e,g as n}from"./app-959684f7.js";import{_ as l}from"./AppLandingsPage-a79d9075.js";import"./HeaderImage-20670ffd.js";import"./_plugin-vue_export-helper-c27b6911.js";const s=e("section",{class:"dark:bg-gray-900"},[e("div",{class:"py-8 px-4 mx-auto max-w-screen-xl lg:py-16 lg:px-6"},[e("div",{class:"mx-auto max-w-screen-sm text-center lg:mb-16 mb-8"},[e("h2",{class:"mb-4 text-6xl lg:text-4xl tracking-tight font-extrabold text-gray-900 dark:text-white"},[n("Algemene voorwaarden "),e("br"),n(" & "),e("br"),n(" Privacy Policy")])]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 1 Algemene Bepalingen "),e("p",{class:"my-4 text-gray-500 leading-relaxed space-y-8 sm:space-y-4 text-lg"},[n(" 1.1 Op deze pagina vindt u de algemene voorwaarden van PadelBuddies, zoals deze beschikbaar is gesteld door PadelBuddies. In deze algemene voorwaarden geven wij aan onder welk voorbehoud wij de informatie op onze website aan u aanbieden. "),e("br"),n(" 1.2 Alle algemene voorwaardes zijn pas van toestemming wanneer er akkoord is gegeven vanuit onze website. "),e("br"),n(" 1.3 Deze algemene voorwaarden zijn van toepassing op alles wat we doen bij PadelBuddies, inclusief maar niet beperkt tot aanbiedingen en overeenkomsten tussen ons en onze opdrachtgevers. "),e("br"),n(" 1.4 Als we afwijken van deze algemene voorwaarden, moet dat schriftelijk en uitdrukkelijk tussen ons en de opdrachtgever overeengekomen zijn. "),e("br"),n(" 1.5 Als één of meer bepalingen in deze algemene voorwaarden ongeldig, nietig of vernietigd worden verklaard, blijven de andere bepalingen van kracht. We vervangen dan de ongeldige, nietige of vernietigde bepalingen door nieuwe bepalingen die zoveel mogelijk overeenkomen met de ongeldige, nietige of vernietigde bepalingen. "),e("br"),n(" 1.6 Deze algemene voorwaarden zijn niet van toepassing als we in de overeenkomst tussen ons en de Opdrachtgever daarvan afwijken. "),e("br"),n(" 1.7 PadelBuddies heeft het recht deze algemene voorwaarden te wijzigen en zal Opdrachtgever hiervan op de hoogte stellen. "),e("br")]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 2 Aanbiedingen, Totstandkoming En Wijziging Overeenkomst "),e("p",{class:"my-4 text-gray-500 leading-relaxed space-y-8 sm:space-y-4 text-lg"},[n(" 2.1 Een overeenkomst komt alleen tot stand als we en onze Opdrachtgever schriftelijk overeenstemming bereiken. "),e("br"),n(" 2.2 Als we eenmaal een overeenkomst hebben, kan die alleen worden gewijzigd als we daarover schriftelijk overeenstemming bereiken met de Opdrachtgever. We kunnen bijvoorbeeld alleen schriftelijk akkoord gaan met wijzigingen in de hoeveelheid of de prijs van de producten of diensten die we leveren."),e("br"),n(" 2.3 Bij PadelBuddies doen we ons best om de beste aanbiedingen te doen aan onze opdrachtgevers, maar al onze aanbiedingen zijn vrijblijvend, tenzij we in het aanbod uitdrukkelijk anders aangeven. "),e("br"),n(" 2.4 PadelBuddies is slechts aan een aanbod gebonden indien deze door de opdrachtgever schriftelijk binnen 30 dagen wordt bevestigd. Niettemin heeft PadelBuddies het recht een overeenkomst met een (potentiële) opdrachtgever om een voor PadelBuddies gegronde reden te weigeren. "),e("br"),n(" 2.5 Aanbiedingen of offertes gelden niet automatisch voor vervolgopdrachten. "),e("br"),n(" 2.6 Levertijden in het aanbod van PadelBuddies zijn in beginsel indicatief en geven de opdrachtgever bij overschrijding ervan geen recht op ontbinding of schadevergoeding, tenzij duidelijk anders is overeengekomen. "),e("br"),n(" 2.7 PadelBuddies heeft het recht om de (ondertekende) overeenkomst te herroepen binnen 5 werkdagen na ontvangst van de aanvaarding. "),e("br"),n(" 2.8 Indien de opdrachtgever een reeds bevestigde opdracht annuleert, worden de reeds daadwerkelijk gemaakte kosten (inclusief de bestede tijd) in rekening gebracht bij de opdrachtgever. "),e("br"),n(" 2.9 De opdrachtgever zijnde een consument heeft gedurende de wettelijke termijn van 14 dagen recht op herroeping, tenzij PadelBuddies met toestemming van de opdrachtgever reeds aanvang heeft gemaakt met de dienstverlening. De opdrachtgever ziet door middel van deze toestemming af van haar herroepingsrecht. "),e("br")]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 3 Aansprakelijkheid "),e("p",{class:"my-4 text-gray-500 leading-relaxed space-y-8 sm:space-y-4 text-lg"},[n(" 3.1 Beperkingen op aansprakelijkheid. PadelBuddies is niet aansprakelijk voor schade behalve in de gevallen beschreven in dit artikel. "),e("br"),n(" 3.2 Wij als PadelBuddies kunnen alleen aansprakelijk gesteld worden als door ons systeem een baan al bezet blijkt te zijn bij aankomst. In dit geval zijn wij jullie het volledige bedrag schuldig terug over te maken. "),e("br"),n(" 3.3 Bij PadelBuddies doen we ons best om de beste aanbiedingen te doen aan onze opdrachtgevers, maar al onze aanbiedingen zijn vrijblijvend, tenzij we in het aanbod uitdrukkelijk anders aangeven."),e("br"),n(" 3.4 Overmacht. PadelBuddies is niet aansprakelijk indien een tekortkoming het gevolg is van overmacht. "),e("br"),n(" 3.5 Uitzondering op beperkingen. De in dit artikel opgenomen beperkingen gelden niet indien de schade het gevolg is van opzet of grove schuld van PadelBuddies of haar leidinggevende ondergeschikten."),e("br"),n(" 3.6 Wij als PadelBuddies zijn in geen enkel geval aansprakelijk te stellen wanneer er niet genoeg materialen aanwezig zijn op de verenging die zijn banen verhuurd via onze website. "),e("br"),n(" 3.7 Wijn kunnen aansprakelijk gesteld worden in de volgende gevallen, maar is niet beperkt tot hetgeen daaromtrent in de wet en jurisprudentie wordt begrepen, (i) het niet naar behoren nakomen van verplichtingen van (toe)leveranciers, (ii) gebrekkigheid van zaken, apparatuur, programmatuur of materialen van derden, (iii) overheidsmaatregelen, (iv) elektriciteitsstoring, (v) storing van internet, datanetwerk- en telecommunicatie faciliteiten (bijvoorbeeld door: cybercriminaliteit en hacking), (vi) brand, (vii) natuurrampen, (viii) oorlog en terroristische aanslagen, (ix) algemene vervoersproblemen, (x) werkstakingen in het bedrijf van PadelBuddies en (xi) overige situaties die naar het oordeel van PadelBuddies buiten haar invloedssfeer vallen die de nakoming van haar verplichtingen tijdelijk of blijvend verhinderen. "),e("br"),n(" 3.8 PadelBuddies sluit uitdrukkelijk alle aansprakelijkheid uit voor gevolgschade. PadelBuddies is niet aansprakelijk voor indirecte schade, bedrijfsschade, winstderving en/of geleden verlies, gemiste besparingen, schade door bedrijfsstagnatie, vermogensverliezen, vertragingsschade, renteschade en immateriële schade. "),e("br"),n(" 3.9 PadelBuddies is niet aansprakelijk voor diefstal en/of beschadiging of vermissing van (persoonlijke) eigendommen in en rondom de locaties die wij aanbieden. "),e("br"),n(" 3.10 Ook is PadelBuddies niet aansprakelijk voor lichamelijk letsel ontstaan door blessures, deelname aan een potje padel en/of ongelukken. Elke deelname van de opdrachtgever c.q. deelnemer vindt plaats op eigen risico en verantwoordelijkheid van de deelnemende persoon. Opdrachtgever/deelnemer is zelf verantwoordelijk voor de keuze die hij/zij maakt om deel te nemen aan een potje padel. "),e("br"),n(" 3.11 PadelBuddies is niet aansprakelijk voor het feit dat de gebruiker de accountinformatie niet, niet juist en/of niet tijdig heeft ontvangen. Nimmer is PadelBuddies aansprakelijk indien gebruiker zijn inlog- en/of accountgegevens niet op een veilige plaats heeft bewaard. "),e("br"),n(" 3.12 In geen geval is PadelBuddies verantwoordelijk voor fouten en/of onregelmatigheden gebruik van de applicatie en is niet aansprakelijk voor storingen of het om welke reden dan ook niet beschikbaar zijn van de applicatie of het verlies en/of verminkt raken van de gegevens en informatie van gebruiker. "),e("br")]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 4 Verwerking van persoonsgegevens "),e("p",{class:"my-4 text-gray-500 leading-relaxed space-y-8 sm:space-y-4 text-lg"},[n(" 4.1 Wij verzamelen persoonsgegevens uitsluitend voor verwerkingen van reserveringen. "),e("br"),n(" 4.2 De ve Verwerker neemt passende technische en organisatorische maatregelen zodat de verwerking aan de vereisten van de AVG voldoet en de bescherming van de rechten van de betrokkenen zijn gewaarborgd. PadelBuddies zal de persoonsgegevens niet voor eigen doeleinden gebruiken. "),e("br"),n(" 4.3 De verwerker schakelt geen Subverwerker(s) in zonder voorafgaande schriftelijke toestemming van / of overeenkomst met Verwerkingsverantwoordelijke. "),e("br"),n(" 4.4 De verwerker legt aan een Subverwerker in een Subverwerkersovereenkomst minimaal dezelfde verplichtingen vast als in deze overeenkomst. "),e("br"),n(" 4.5 PadelBuddies gaat zorgvuldig om met de (persoons)gegevens van de opdrachtgever en/of de bezoekers van de website en zal deze slechts conform de geldende normen gebruiken. Indien daartoe gevraagd zal PadelBuddies de betrokkene hierover informeren. "),e("br"),n(" 4.6 De gegevens van gebruikers worden nooit langer dan 3 jaar bewaard, na deze tijd zal de gebruiker opnieuw een account moeten aanmaken wanneer de gebruiker gebruik wil maken van onze diensten. "),e("br")]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 5 Verwijderen van persoonsgegevens "),e("p",{class:"my-4 text-gray-500 leading-relaxed space-y-8 sm:space-y-4 text-lg"},[n(" 5.1 De gebruiker mag ten allen tijden vragen of de persoonsgegevens verwijderd worden die binnen ons bewaartermijn vallen. "),e("br"),n(" 5.2 De gebruiker mag ten alle tijden vragen wat we met persoonsgegevens doen. "),e("br")]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 6 Programmatuur "),e("p",{class:"my-4 text-gray-500 leading-relaxed space-y-8 sm:space-y-4 text-lg"},[n(" 6.1 Alle geschreven code is uitsluitend bedoel voor PadelBuddies. "),e("br"),n(" 6.2 Klant mag de programmatuur uitsluitend in en ten behoeve van zijn eigen bedrijf of organisatie gebruiken en in zulks uitsluitend voor zover dat noodzakelijk is voor het beoogd gebruik. Klant zal de programmatuur niet gebruiken ten behoeve van derden, bijvoorbeeld in het kader van ‘Software as a service’ (SaaS) of ‘outsourcing’. "),e("br")]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 7 Prijzen en betalingen "),e("p",{class:"my-4 text-gray-500 leading-relaxed space-y-8 sm:space-y-4 text-lg"},[n(" 7.1 Alle prijzen zijn in beginsel inclusief omzetbelasting (btw), tenzij anders aangegeven. "),e("br"),n(" 7.2 PadelBuddies voert de overeenkomst uit conform het overeengekomen uurtarief. "),e("br"),n(" 7.3 PadelBuddies is gerechtigd om de geldende prijzen en tarieven jaarlijks te verhogen conform de geldende inflatie tarieven. Overige prijswijzigingen gedurende de overeenkomst zijn slechts mogelijk indien en voor zover deze uitdrukkelijk zijn vastgelegd in de overeenkomst. "),e("br"),n(" 7.4 De opdrachtgever dient deze kosten ineens, zonder verrekening of opschorting, te voldoen op het aan haar kenbaar gemaakte rekeningnummer en gegevens van PadelBuddies. "),e("br"),n(" 7.5 Tijdens de geldigheidsduur van het aanbod worden de prijzen van de aangeboden zaken niet verhoogd, behoudens het geval dat er veranderingen zijn in btw-tarieven. "),e("br")]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 8 Klachten "),e("p",{class:"my-4 text-gray-500 leading-relaxed space-y-8 sm:space-y-4 text-lg"},[n(" 8.1 Indien Opdrachtgever niet tevreden is over de service van PadelBuddies of anderszins klachten heeft over de uitvoering van zijn opdracht, is Opdrachtgever verplicht om deze klachten zo spoedig mogelijk, maar uiterlijk binnen 7 kalenderdagen na de betreffende aanleiding dat tot de klacht heeft geleid te melden. Klachten kunnen mondeling of schriftelijk gemeld worden via padelbuddies@gmail.com met als onderwerp “Klacht”. "),e("br"),n(" 8.2 De klacht moet door Opdrachtgever voldoende onderbouwd en/of toegelicht zijn, wil PadelBuddies de klacht in behandeling kunnen nemen. "),e("br"),n(" 8.3 PadelBuddies zal zo spoedig mogelijk, doch uiterlijk binnen 7 kalenderdagen na ontvangst van de klacht inhoudelijk reageren op de klacht. "),e("br"),n(" 8.4 Partijen zullen proberen om gezamenlijk tot een oplossing te komen. "),e("br")]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 9 Uitvoering van dienstverlening "),e("p",{class:"my-4 text-gray-500 leading-relaxed space-y-8 sm:space-y-4 text-lg"},[n(" 9.1 Eenieder dient zich te houden aan de opgegeven huisregels van aangesloten verenigingen. Overtreding hiervan kan tot gevolg hebben dat de Overeenkomst wordt ontbonden en Opdrachtgever de toegang tot de locatie van de specifieke vereniging wordt ontzegd. "),e("br")]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 10 Reserveringen "),e("p",{class:"my-4 text-gray-500 leading-relaxed space-y-8 sm:space-y-4 text-lg"},[n(" 10.1 Opdrachtgever kan door middel van de website van PadelBuddies een padelbaan reserveren. Opdrachtgever dient zelf het tijdstip aan te klikken, waarna opdrachtgever de bijbehorende prijs hierbij ziet. "),e("br"),n(" 10.2 Opdrachtgever kan per account maximaal drie boekingen per dag plaatsen. "),e("br"),n(" 10.3 Opdrachtgever kan maximaal twee weken van tevoren reserveren. PadelBuddies is gerechtigd de boeking te laten vervallen indien er tussendoor een evenement wordt georganiseerd. Opdrachtgever kan de vervallen boeking zelf verplaatsen naar een andere gewenste datum. "),e("br"),n(" 10.4 Annulering tot 24 uur voor ingangsdatum is kosteloos. Annuleringen dienen altijd via de app te geschieden. "),e("br"),n(" 10.5 Als er wegens slechte weersomstandigheden de afspraak niet kan plaatsvinden, kan PadelBuddies de afspraak afzeggen en/of annuleren. PadelBuddies zal het geld terugstorten. "),e("br"),n(" 10.6 Als het reserveren via de website niet mogelijk is, omdat deze niet beschikbaar is, kan opdrachtgever contact opnemen met PadelBuddies voor het plaatsen van een reservering. "),e("br")]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 11 Verplichtingen "),e("p",{class:"my-4 text-gray-500 leading-relaxed sm:space-y-4 text-lg"},[n(" 11.1 Opdrachtgever heeft een zelfstandige verantwoordelijkheid voor het beheer en gebruik van de door aangeboden diensten (op)geleverde zaken. "),e("br"),n(" 11.2 Doorverhuur is verboden tenzij opdrachtgever expliciet schriftelijk toestemming van PadelBuddies heeft om het gehuurde door te verhuren aan derden. "),e("br"),n(" 11.3 Opdrachtgever is verplicht het gehuurde zodanig te gebruiken, dat door of vanwege de opdrachtgever en door (het gebruik van) het gehuurde geen hinder of overlast in welke vorm dan ook wordt aangedaan aan de door ons aangeboden locaties, daarvan de buren en de verdere omgeving. "),e("br"),n(" 11.4 Opdrachtgever is verplicht het Gehuurde na afloop van de huurperiode in goede staat ter beschikking te stellen aan de door ons aangeboden locaties af te leveren conform de Overeenkomst. "),e("br"),n(" 11.5 PadelBuddies staat ervoor in dat het Gehuurde voldoet aan de Overeenkomst zoals tussen partijen is gesloten. "),e("br"),n(" 11.6 Opdrachtgever is zelf aansprakelijk voor de hiernavolgende kosten en schades, en vrijwaart PadelBuddies van: "),e("ul",{class:"pl-10 list-disc"},[e("li",null,"Schade door het verlies van het Gehuurde zijnde materialen en/of de daarbij behorende documenten."),e("li",null,"De schade die om welke reden dan ook niet door de verzekeringsmaatschappij van Opdrachtgever wordt vergoed."),e("li",null,"Schade door onzorgvuldig handelen van Opdrachtgever zelf.")])]),e("h2",{class:"mb-4 text-2xl lg:text-2xl tracking-tight font-extrabold text-gray-900 dark:text-white text-center sm:text-left"}," Artikel 12 Gebruik van applicatie "),e("p",{class:"my-4 text-gray-500 leading-relaxed sm:space-y-4 text-lg"},[n(" 12.1 PadelBuddies stelt de applicatie ter beschikking aan Gebruiker. De uitgevoerde diensten worden uitgevoerd op basis van een inspanningsverbintenis. Gebruiker heeft toegang tot de applicatie nadat een account heeft aangemaakt. "),e("br"),n(" 12.2 Gebruiker is altijd verantwoordelijk voor alle gegevens en informatie die op haar account en/of de applicatie plaats. Als Gebruiker vermoedt dat de verstrekte gegevens onjuist of onvolledig zijn, informeert Gebruiker PadelBuddies en zal alsnog de juiste informatie verstrekken. De Gebruiker dient zelf de gegevens up-to-date te houden en daartoe in zijn eigen accountgegevens aanpassen. "),e("br"),n(" 12.3 Gebruiker heeft verantwoordelijkheid voor het gebruik van de applicatie. Gebruiker is verplicht zich te houden aan hiernavolgende reglementen bij gebruik van de applicatie. Gebruiker dient zich te onthouden van het gebruik van de applicatie: "),e("ul",{class:"pl-10 list-disc"},[e("li",null,"Om handmatige of geautomatiseerde programmatuur, apparatuur of andere processen te gebruiken om de binnen de applicatie gebruikte gegevens op het internet te indexeren of te scrapen."),e("li",null,"Op een manier dat sprake is van illegale activiteiten of activiteiten die in strijd zijn met de goede zeden of de openbare orde."),e("li",null,"Het kopiëren van (onderdelen van) de applicatie van PadelBuddies."),e("li",null,"Om op andere wijze schade toe te brengen aan de belangen van PadelBuddies.")]),e("br"),n(" 12.4 PadelBuddies is gerechtigd aangifte te doen in het geval van (mogelijke) strafbare handelingen en de verstrekte gegevens aan de bevoegde instanties te overhandigen, alsmede alle handelingen te verrichten die van haar gevraagd worden in het kader van het onderzoek. PadelBuddies heeft recht Gebruiker de toegang tot de PadelBuddies applicatie te ontzeggen en/of het beëindigen van PadelBuddies applicatie. "),e("br"),n(" 12.5 Naast de verplichtingen uit de wet, schade welke ontstaat door ondeskundigheid of het niet handelen conform bovenstaande punten voor rekening en risico van Gebruiker. "),e("br"),n(" 12.6 Gebruiker is zelf verantwoordelijk voor de juiste beveiliging van het (mobiele) apparaat waarop hij de PadelBuddies applicatie gebruikt, alsmede voor het beveiligen en geheimhouden van zijn/haar eigen inloggegevens. "),e("br"),n(" 12.7 Elke Gebruiker dient een account aan te maken ten behoeve van de toegang en het gebruik van de applicatie. "),e("br"),n(" 12.8 Het account kan geregistreerd worden met een e-mailadres en een wachtwoord, tenzij anders aangegeven. "),e("br"),n(" 12.9 De Gebruiker is verplicht om tijdens de registratie van het account juiste en volledige informatie te verstrekken en om het account te allen tijde up-to-date houden. "),e("br"),n(" 12.10 Het is niet toegestaan de inloggegevens aan derden te verstrekken. "),e("br"),n(" 12.11 De Gebruiker is zelf verantwoordelijk voor zijn of haar inloggegevens en dient deze inloggegevens niet aan derden te verstrekken. Indien een Gebruiker vermoedt dat de inloggegevens zijn kwijtgeraakt, gestolen of mogelijk sprake is van ongeoorloofd gebruik van het account, dient Gebruiker per direct contact op te nemen met PadelBuddies. Gebruiker is zelf aansprakelijk voor alle activiteiten die worden uitgevoerd via het eigen account, tenzij de Gebruiker kan aantonen dat hij/zij niet nalatig is geweest. Hieronder wordt in ieder geval verstaan: het niet melden van het ongeoorloofde gebruik of verlies van inloggegevens. "),e("br")])])],-1),u={__name:"TermsOfService",props:{terms:String},setup(g){return(v,k)=>(r(),a(l,null,{default:t(()=>[i(d(o),{title:"Terms of Service"}),s]),_:1}))}};export{u as default};
